document.addEventListener("DOMContentLoaded",()=>{const e={root:null,rootMargin:"0px",threshold:.1},t=new IntersectionObserver((e,t)=>{e.forEach(e=>{e.isIntersecting&&(e.target.classList.add("is-visible"),t.unobserve(e.target))})},e),n=document.querySelectorAll(".glass-card, .section-heading, .eyebrow, .pill, p, h1, h2, h3");n.forEach(e=>{e.classList.add("reveal-on-scroll"),t.observe(e)})})